import React, { useState, useContext } from 'react';
import { useHistory, useLocation } from 'react-router-dom';
import { ProductsContext } from '../../ProductsProvider';
import classNames from 'classnames';
import './Pagination.scss';

export const Pagination = () => {
  const { productsList, pageItems, page, setPage, path } = useContext(ProductsContext);
  // const [pagination, setPagination] = useState({
  //   total: productsList[path],
  //   page: page[path],
  //   perPage: pageItems[path],
  // })

  const lastPage = productsList[path] / pageItems[path];
  //console.log(pagination);
  const location = useLocation();
  const history = useHistory();
  const searchParams = new URLSearchParams(location.search);
  const changeHistory = (name, value) => {
    searchParams.set(name, value);
    history.push(`?${searchParams.toString()}`);
  };

  return (
    <div className="Pagination">
       <button
          type="button"
          className={classNames('Pagination-Prev', {
            disabled_prev: page[path] === 1
          })}
          disabled={page[path] === 1}
          onClick={() => {
            setPage({...page, [path]: page[path] - 1});
            changeHistory('page', page[path] - 1);
          }}
        >
          Prev
        </button>
        <ul className="Pagination-List">
          {[...Array(lastPage).keys()]
            .map(val => val + 1)
            .map((item, index) => (
              <li
                className="Pagination-Item"
                key={`${index + 1}`}
              >
                {item}
              </li>
          ))}
        </ul>
        <button
          type="button"
          className={classNames('Pagination-Next', {
            disabled_next: page[path] === lastPage
          })}
          disabled={page[path] === lastPage}
          onClick={() => {
            setPage({...page, [path]: page[path] + 1});
            changeHistory('page', page[path] + 1);
          }}
        >
          Prev
        </button>
    </div>
  );
}
