import React, {useContext} from 'react';
import { NavLink } from 'react-router-dom';
import { ProductsContext } from '../../ProductsProvider';

export const Favourites = () => {
  const { favorites } = useContext(ProductsContext);

  return (
    <NavLink className="SectionRight SectionRight-Favorites" to="/favorites">
      <div className="SectionRight-Content">
        <img
          className="SectionRight-Link"
          src="./img/favourites.svg"
          alt="Favorites"
        />
        {favorites.length !== 0 && (
          <span className="SectionRight-Count">{favorites.length}</span>
        )}
      </div>
    </NavLink>
  )
}
