import React from 'react';
import classNames from 'classnames';
import PropTypes from 'prop-types';
import './SelectCarts.scss';

export const SelectCarts = ({
  toggle,
  setToggle,
  option,
  setOption,
  list,
}) => (
  <div className="SelectCarts">
    {/* <div className="SelectCarts-Container"> */}
      <p className="SelectCarts-Text">{option}</p>
      {!toggle ? (
        <button
          className="SelectCarts-Button"
          type="button"
          onClick={() => setToggle(true)}
        >
          <img src="./img/down.svg" alt="Down"/>
        </button>
      ) : (
        <button
          className="SelectCarts-Button"
          type="button"
          onClick={() => setToggle(false)}
        >
          <img src="./img/up.svg" alt="Up"/>
        </button>
      )}
    {/* //</div> */}
    <ul className={classNames('SelectCarts-List', {
        visible_list: !toggle,
      })}
    >
      {list.map(item => (
        <li
          className={classNames('SelectCarts-Item', {
            selected: item === option,
          })}
          key={item}
          onClick={() => setOption(item)}
        >
          {item}
        </li>
      ))}
    </ul>
  </div>
)

SelectCarts.propTypes = {
  toggle: PropTypes.ob.isRequired,
  setToggle: PropTypes.func.isRequired,
  option: PropTypes.string.isRequired,
  setOption: PropTypes.func.isRequired,
  list: PropTypes.array.isRequired,
}
