import React, { useMemo, useState, useEffect, useCallback } from 'react';
import { useLocation, useHistory, useParams } from 'react-router-dom';
import { PropTypes } from 'prop-types';
import products from './data/products.json';
import { debounce } from './helpers/debounce';
import { initialValue } from './helpers/search';
import { initialSortSelect, initialToggleSelect, initialItemsPageSelect } from './helpers/select';
import { initialPage } from './helpers/pagination';


export const ProductsContext = React.createContext({
  favourites: [],
  setFavorites: () => {},
  carts: [],
  setCarts: () => {},
  productsList: {},
  appliedValue: {},
  setAppliedValue: () => {},
  applyValue: () => {},
  path: '',
  sortCarts: {},
  setSortCarts: () => {},
  setToggleSort: () => {},
  toggleSort: {},
  pageItems: {},
  setPageItems: () => {},
  toggleItemsPage: {},
  setToggleItemsPage: () => {},
  page: {},
  setPage: () => {},
});
const root = document.getElementById('root');

export const ProductsProvider = ({ children }) => {
  const [productsList] = useState({
    phones: products,
    tablets: products.slice(0, 10),
    accessories: products.slice(0, 5),
  });
  const [appliedValue, setAppliedValue] = useState(initialValue);
  const applyValue = useCallback(debounce(setAppliedValue, 1000), []);
  const [sortCarts, setSortCarts] = useState(initialSortSelect);
  const [toggleSort, setToggleSort] = useState(initialToggleSelect);
  const [pageItems, setPageItems] = useState(initialItemsPageSelect)
  const [toggleItemsPage, setToggleItemsPage] = useState(initialToggleSelect);
  const [page, setPage] = useState(initialPage);
  const { pathname, search } = useLocation();
  const path = pathname.slice(1);
  const history = useHistory();
  const searchParams = new URLSearchParams(search);
  const { productId } = useParams();
  console.log(history, search);
  console.log( searchParams.get('model'))
  console.log(productId)

  const [favorites, setFavorites] = useState(
    JSON.parse(localStorage.getItem('favorites')) || []
  );

  const [carts, setCarts] = useState(
    JSON.parse(localStorage.getItem('carts')) || []
  );
  // console.log(favorites.filter);
  // console.log(products.filter(item => favorites.includes(item.id)));

  useEffect(() => (
    localStorage.setItem('favorites', JSON.stringify(favorites))
  ), [favorites]);

  useEffect(() => (
    localStorage.setItem('carts', JSON.stringify(carts))
  ), [carts]);

  useEffect(() => root.scrollIntoView(), [path]);

  useMemo(() => {
    if (sortCarts[path]) {
      searchParams.set('sort',
        sortCarts[path] === 'Alphabetically'
        ? 'name': sortCarts[path] === 'Newest'
        ? 'age' : 'price');
    }

    if (!['home', 'product', 'favorites', 'cart'].includes(path)) {
      if (pageItems[path] !== 'All items') {
        searchParams.set('perPage', pageItems[path]);
        searchParams.set('page', page[path]);
      } else {
        searchParams.delete('perPage');
        searchParams.delete('page');
      }
    }

    if (!['home', 'product', 'cart'].includes(path)) {
      if (appliedValue[path]) {
        searchParams.set('query', appliedValue[path]);
        searchParams.delete('sort');
        searchParams.delete('perPage');
        searchParams.delete('page');
      } else {
        searchParams.delete('query');
      }
    }

    history.push(`${path}?${searchParams.toString()}`);

  },[sortCarts[path], pageItems[path], page[path], appliedValue[path]]);

  const contextValue = useMemo(() => (
    {
      favorites,
      setFavorites,
      carts,
      setCarts,
      productsList,
      appliedValue,
      setAppliedValue,
      applyValue,
      path,
      sortCarts,
      setSortCarts,
      toggleSort,
      setToggleSort,
      pageItems,
      setPageItems,
      toggleItemsPage,
      setToggleItemsPage,
      page,
      setPage,
    }),[ favorites, carts, productsList,
    appliedValue,applyValue, setAppliedValue,
    path, sortCarts, toggleSort,
    pageItems, toggleItemsPage, page
  ]);


  return (
    <ProductsContext.Provider value={contextValue}>
      {children}
    </ProductsContext.Provider>
  )
}

ProductsProvider.propTypes = {
  children: PropTypes.array.isRequired,
};
