import React, { useContext } from 'react';
import classNames from 'classnames';
import PropTypes from 'prop-types';
import './SelectSort.scss';
import { ProductsContext } from '../../ProductsProvider';

export const SelectSort = ({ list }) => {

  const { path, setPage, page, toggleSort, setToggleSort, sortCarts, setSortCarts } = useContext(ProductsContext);

  return (
    <div className="SelectCarts">
      <p className="SelectCarts-Text">{sortCarts[path]}</p>
      {!toggleSort[path] ? (
        <button
          className="SelectCarts-Button"
          type="button"
          onClick={() => {
            setToggleSort({...toggleSort, [path]: true});
          }}
        >
          <img src="./img/down.svg" alt="Down"/>
        </button>
      ) : (
        <button
          className="SelectCarts-Button"
          type="button"
          onClick={() => setToggleSort({...toggleSort, [path]: false})}
        >
          <img src="./img/up.svg" alt="Up"/>
        </button>
      )}
      <ul className={classNames('SelectCarts-List', {
          visible_list: !toggleSort[path],
        })}
      >
        {list.map(item => (
          <li
            className={classNames('SelectCarts-Item', {
              selected: item === sortCarts[path],
            })}
            key={item}
            onClick={() => setSortCarts({...sortCarts, [path]: item})}
          >
            {item}
          </li>
        ))}
      </ul>
    </div>
  );
}

SelectSort.propTypes = {
  list: PropTypes.array.isRequired,
}
