import React, { useContext } from 'react';
import { ProductsContext } from '../../ProductsProvider';
import { PropTypes } from 'prop-types';
import './Buttons.scss';

export const Buttons = ({ id, price, name, image }) => {
  const { carts, setCarts, favorites, setFavorites } = useContext(ProductsContext);

  return (
    <div className="Buttons">
      {!carts.find(item => item.id === id) ? (
        <button
          type="button"
          className="Buttons-ButtonAdd"
          onClick={() => {setCarts([
             ...carts,
             {id, price, count: 1, name, image}])}}
        >
          Add to cart
        </button>
      ) : (
        <button
          type="button"
          className="Buttons-ButtonAdded"
        >
          Added to cart
        </button>
      )}
      {!favorites.includes(id) ? (
        <button
        type="button"
        className="Buttons-ButtonFavorites"
        onClick={() => setFavorites([ ...favorites, id ])}
      >
        <img src="./img/favorites.svg" alt="Favorites"/>
      </button>
      ) : (
        <button
          type="button"
          className="Buttons-ButtonFavorites Buttons-ButtonFavorites_selected"
          onClick={() => setFavorites(favorites.filter(item => item !== id))}
        >
          <img src="./img/favorites-selected.svg" alt="Favorites"/>
        </button>
      )}
    </div>
  )
};

Buttons.propTypes = {
  id: PropTypes.number.isRequired,
  price: PropTypes.number.isRequired,
  name: PropTypes.string.isRequired,
  image: PropTypes.string.isRequired,
}
