import React, { useContext, useState, useCallback } from 'react';
import { useLocation } from 'react-router-dom';
import classNames from 'classnames';
import { setValueInput, initialValue } from '../../helpers/search';
import { debounce } from '../../helpers/debounce';
import './Search.scss';
import { ProductsContext } from '../../ProductsProvider';

export const Search = () => {
  const { pathname } = useLocation();
  //console.log(pathname)
  const [value, setValue] = useState(initialValue);
  // const [appliedValue, setAppliedValue] = useState(initialValue);
  // const applyValue = useCallback(debounce(setAppliedValue, 1000), []);

  const {visibleSearch} = useContext(ProductsContext);

  return (
    <div className={classNames('Search', {
      search_hidden: ['home', 'cart'].includes(pathname.slice(1)),
      })}
    >
      {/* visibleSearch || */}
      <input
        className="Search-Input"
        type="text"
        name={pathname.slice(1)}
        placeholder={`Search in ${pathname.slice(1)}...`}
        value={setValueInput(pathname, value)}
        onChange={e => {
          setValue({...value,[pathname.slice(1)]: e.target.value});
          applyValue({...appliedValue, [pathname.slice(1)]: e.target.value});
      }}
      />
      {value[pathname.slice(1)] ? (
        <button
          className="Search-ButtonClear button-search"
          type="button"
          onClick={() => {
            setValue({...value,[pathname.slice(1)]: ''});
            setAppliedValue({...appliedValue,[pathname.slice(1)]: ''});
        }}
          tabIndex
        >
          <img
            src="./img/clear.svg"
            alt="Clear"
          />
        </button>
      ) : (
      <button
        className="Search-ButtonSearch button-search"
        type="button"
        tabindex="0"
        onClick={() => {console.log('hello')}}
        tabindex="0"
      >
        <img
          src="./img/search.svg"
          alt="Search"
        />
      </button>
      )}
    </div>
  )
}
