import React , { useState } from 'react';
import classNames from 'classnames';
import './CartSlider.scss'; 

const getSliderWidth = () => (
  window.innerWidth > 525 ? Math.ceil((window.innerWidth * 72.2/100)+ 96) : Math.ceil((window.innerWidth * 100/100) - 48)
)

export const CartSlider = () => {
  const [cards, setCards] = useState(['1' , '2', '3', '4', '5', '6'])
  const [sliderWidth, setSliderWidth] = useState(getSliderWidth)
  const [ disabled ] = useState('1');
  const [currentPosition, setCurrentPosition] = useState(0);
  const handleResize = () => {
    setSliderWidth(getSliderWidth());
  }
  const handleClickPrev = () => {
    window.addEventListener('resize', handleResize);
    let position = currentPosition + sliderWidth;

    if (position > 0) {
      position = 0;
    }

  }

  return (
    <div className="CartSlider">
      <div className="CartSlider-Container">
        <p className="CartSlider-Title">Title</p>
        <div className="CartSlider-Buttons">
          <button className={classNames('button_cart', {
            //prev_disabled: disabled,
            button_prev: disabled,
          })}>
            {}
          </button>
          <button className={classNames('button_cart', {
            next_disabled: disabled,
            button_next: !disabled,
          })}>
            {}
          </button>
        </div>
      </div>
      <div className="slider">
        <div className="CartSlider-Content">
        {cards.map(item => (
          <div key={item} className="Cart">
            <img src={`./img/slider/${item}.jpg`} className="Cart-Image"/></div>
        ))}
        </div>
      </div>
    </div>

  )
}
