import React, {useEffect, useState } from 'react';
import './ProductDetailsPage.scss';
import { useHistory, useParams } from 'react-router';
import { NavLink } from 'react-router-dom';
import products from '../../data/products.json';
//import { useContext } from 'react';
//import { ProductsContext } from '../../ProductsProvider';

export const ProductDetailsPage = () => {
  //const { path } = useContext(ProductsContext);
  const [product, setProduct] = useState({});
  const history = useHistory();
  const { productId, type } = useParams() || '';
  console.log(productId, type);

  useEffect(() => (
    setProduct(products.find(item => item.id === productId))
  ), [productId]);

  console.log(product);

  return (
    <div className="ProductDetailsPage">
      <div className="ProductDetailsPage-Breadcrumbs Breadcrumbs">
        <div className="Breadcrumbs-Nav">
          <NavLink className="Breadcrumbs-Link nav" to="/home">
            <img src="./img/home.svg" alt="Home" className="nav"/>
          </NavLink>
          <img src="./img/next-disabled.svg" alt="Next" className="nav"/>
          <NavLink className="Breadcrumbs-Link nav" pa>
            {type[0].toUpperCase() + type.slice(1)}
          </NavLink>
          <img src="./img/next-disabled.svg" alt="Next" className="nav"/>
          <p className="Breadcrumbs-Text nav">{product.name}</p>
        </div>
        <div className="Breadcrumbs-Back">
          <img src="./img/prev.svg" alt="Prev" className="nav"/>
          <button
            className="Breadcrumbs-Buttom nav"
            type="button"
            onClick={() => history.goBack(-1)}
          >
            Back
          </button>
        </div>
      </div>
    </div>
  )
}
