import React, { useContext } from 'react';
import { ProductsContext } from '../../ProductsProvider';
import { NavLink } from 'react-router-dom';
import { CartProduct } from '../CartProduct';
import './FavoritesPage.scss';

export const FavoritesPage = () => {
  const { favorites, productsList, setVisibleSearch } = useContext(ProductsContext);

  return (
    <div className="FavoritesPage">
      <div className="FavoritesPage-Nav">
        <NavLink
          className="FavoritesPage-Link nav" 
          to="/home"
          onClick={() => setVisibleSearch(false)}
        >
          <img src="./img/home.svg" alt="Home" className="nav"/>
        </NavLink>
        <img src="./img/next-disabled.svg"/>
        <p className="nav">Favorites</p>
      </div>
      <div className="FavoritesPage-Main">
        <h1 className="FavoritesPage-Title">Favorites</h1>
        <p className="FavoritesPage-Count">{favorites.length} items</p>
        <div className="FavoritesPage-Content">
          {productsList
            .filter(product => favorites.includes(product.id))
            .map(product => <CartProduct {...product} />)
          }
        </div>

      </div>
    </div>
  )
}
