import React, { useContext } from 'react';
import { NavLink } from 'react-router-dom';
import { ProductsContext } from '../../ProductsProvider';

export const Cart = () => {
  const { carts, setVisibleSearch } = useContext(ProductsContext);

  return (
    <NavLink
      className="SectionRight SectionRight-Cart"
      to="/cart"
      onClick={() => setVisibleSearch(true)}
    >
      <div className="SectionRight-Content">
        <img
          className="SectionRight-Link"
          src="./img/cart.svg"
          alt="Cart"
        />
        {carts.length > 0 && (
          <span className="SectionRight-Count">{carts.length}</span>
        )}
      </div>
    </NavLink>
  )
}
