import React, { useContext, useMemo, useState } from 'react';
import { ProductsContext } from '../../ProductsProvider';
import PropTypes from 'prop-types';
import { NavLink } from 'react-router-dom';
import { CartProduct } from '../CartProduct';
import { SelectCarts } from '../SelectCarts';
import './PageContent.scss';
import classNames from 'classnames';

export const PageContent = ({ products, title }) => {
  const {appliedValue, path } = useContext(ProductsContext);
  const [sortCarts, setSortCarts] = useState('Alphabetically')
  const [toggleSort, setToggleSort] = useState(false);
  const visibleProducts = useMemo(() => (
    appliedValue[path]
      ?  products.filter(product => product.name.toLowerCase()
          .includes(appliedValue[path].toLowerCase()))
      :  products
  ), [appliedValue[path], products])

  return (
    <div className="PageContent">
      {!appliedValue[path] && (
        <div className="PageContent-Nav">
          <NavLink className="PageContent-Link nav" to="/home">
            <img src="./img/home.svg" alt="Home" className="nav"/>
          </NavLink>
          <img src="./img/next-disabled.svg" alt="Next" className="nav"/>
          <p className="nav">{path[0].toUpperCase() + path.slice(1)}</p>
        </div>
      )}
      <div className="PageContent-Main">
        {!appliedValue[path] && (
          <>
            <h1 className="PageContent-Title">{title}</h1>
            <p className="PageContent-Count">
            {visibleProducts.length} items
            </p>
          </>
        )}
        {appliedValue[path] && (
          <p className="PageContent-Results">
          {visibleProducts.length} results
          </p>
        )}
        <div className="PageContent-Selects">
          <SelectCarts
            toggle={toggleSort}
            setToggle={setToggleSort}
            option={sortCarts}
            setOption={setSortCarts}
            list={['Alphabetically', 'Price hight to low', 'Price low to hight']}
          />
          <div className="PageContent-PageItems">
            <SelectCarts
              toggle={toggleSort}
              setToggle={setToggleSort}
              option={sortCarts}
              setOption={setSortCarts}
              list={['Alphabetically', 'Price hight to low', 'Price low to hight']}
            />
          </div>
          {/* <div className="PageContent-SelectCarts SelectCarts">
            <div className="SelectCarts-Container">
              <p className="SelectCarts-Text">{sortCarts}</p>
              {!toggleSort ? (
                <button
                  className="SelectCarts-Button"
                  type="button"
                  onClick={() => setToggleSort(true)}
                >
                  <img src="./img/down.svg" alt="Down"/>
                </button>
              ) : (
                <button
                  className="SelectCarts-Button"
                  type="button"
                  onClick={() => setToggleSort(false)}
                >
                  <img src="./img/up.svg" alt="Up"/>
                </button>
              )}
            </div>
            <ul className={classNames('SelectCarts-List', {
                visible_list: !toggleSort,
              })}
            >
              {['Alphabetically', 'Price hight to low', 'Price low to hight'].map(item => (
                <li
                  className={classNames('SelectCarts-Item', {
                    selected: item === sortCarts,
                  })}
                  key={item}
                  onClick={() => setSortCarts(item)}
                >
                  {item}
                </li>

              ))}
            </ul> */}
          {/* </div> */}
          <div className="PageContent-SelectItemsPage">{}</div>
        </div>
        <div className="PageContent-Content">
          { visibleProducts.map(product => <CartProduct {...product} key={product.id}/>)}
        </div>

      </div>
    </div>
  )
}

PageContent.propTypes = {
  products: PropTypes.array.isRequired,
  title: PropTypes.string.isRequired,
}
