import React, { useContext } from 'react';
import classNames from 'classnames';
import PropTypes from 'prop-types';
import './SelectCarts.scss';
import { ProductsContext } from '../../ProductsProvider';

export const SelectCarts = ({
  toggle,
  setToggle,
  option,
  setOption,
  list,
}) => {

  const { path, setPage, page } = useContext(ProductsContext);

  return (
    <div className="SelectCarts">
      <p className="SelectCarts-Text">{option[path]}</p>
      {!toggle[path] ? (
        <button
          className="SelectCarts-Button"
          type="button"
          onClick={() => {
            setToggle({...toggle, [path]: true});
            console.log('kat');
          }}
        >
          <img src="./img/down.svg" alt="Down"/>
        </button>
      ) : (
        <button
          className="SelectCarts-Button"
          type="button"
          onClick={() => setToggle({...toggle, [path]: false})}
        >
          <img src="./img/up.svg" alt="Up"/>
        </button>
      )}
      <ul className={classNames('SelectCarts-List', {
          visible_list: !toggle[path],
        })}
      >
        {list.map(item => (
          <li
            className={classNames('SelectCarts-Item', {
              selected: item === option[path],
            })}
            key={item}
            onClick={() => {
              setOption({...option, [path]: item});
              if( !['Alphabetically', 'Price hight to low', 'Price low to hight'].includes([item])) {
              // setPage({...page, [path]: 1})
              };
            }}
          >
            {item}
          </li>
        ))}
      </ul>
    </div>
  );
}

SelectCarts.propTypes = {
  toggle: PropTypes.object.isRequired,
  setToggle: PropTypes.func.isRequired,
  option: PropTypes.object.isRequired,
  setOption: PropTypes.func.isRequired,
  list: PropTypes.array.isRequired,
}
