import React, { useState, useContext } from 'react';
import { useHistory, useLocation } from 'react-router-dom';
import { ProductsContext } from '../../ProductsProvider';
import './Pagination.scss';

export const Pagination = () => {
  const { productsList, pageItems, page, setPage, path } = useContext(ProductsContext);
  const [pagination, setPagination] = useState({
    total: productsList[path],
    page: page[path],
    perPage: pageItems[path],
  })

  const paginationLength = total / perPage;
  console.log(pagination);
  const location = useLocation();
  const history = useHistory();
  const searchParams = new URLSearchParams(location.search);
  const changeHistory = (name, value) => {
    searchParams.set(name, value);
    history.push(`?${searchParams.toString()}`);
  };

  return (
    <div className="Pagination">
       <button
          type="button"
          className={classNames('Pagination-Prev', { disabled: page === 1 })}
          // disabled={page === 1}
          onClick={() => {
            setPage({...page, [path]: page[path] - 1});
            changeHistory('page', page[path] - 1);
          }}
        >
          Prev
        </button>

        <button
          type="button"
          className={classNames('Pagination-', { disabled: page === 1 })}
          // disabled={page === 1}
          onClick={() => {
            setPage({...page, [path]: page[path] - 1});
            changeHistory('page', page[path] - 1);
          }}
        >
          Prev
        </button>
    </div>
  );
}
