import { createSecretKey } from 'crypto';
import React, { useEffect, useState } from 'react';
import { useCallback } from 'react';
import './HomePage.scss';

const initialImages = [
  './img/slider/1.jpg',
  './img/slider/2.jpg',
  './img/slider/3.jpg',
  './img/slider/4.jpg',
  './img/slider/5.jpg',
  './img/slider/6.jpg',
];


export const HomePage = () => {
  const [images, setImages] = useState(initialImages);
  const [ key , setKey ] = useState(1);

  const [ imageWidth, setImageWidth] = useState(Math.ceil(document.documentElement.clientWidth * 72.2 / 100));
  const [ currentPosition, setCurrentPosition] = useState(0);
  const [infinite, setInffinite] = useState(true);
  //const [cu]
  // useEffect(() => {
  //   return setImages(initialImages);
  // }, [])

  console.log(imageWidth);
  const handleClickNext = () => {
    // const maxPosition = (images.length - 1) * imageWidth;
    let position = currentPosition - imageWidth * 1;
    // if(-position < maxPosition) {
    //   // position = currentPosition - imageWidth;
    //   setImages([...images.slice(1), ...images.splice(0, 1)]);
    //   console.log(images);
    //   // position = currentPosition - imageWidth;
    //   // setCount(1);
    // }
    // if(count) {
    //   position = (0 - imageWidth) + imageWidth;
    // }
    // if (!infinite) {
    //   position = -maxPosition;
    // }

    // if (infinite) {
      images.push(...images.splice(0, 1));
      // setImages([...images.slice(1), images.splice(0, 1)]);
    //   console.log(images);
    // }
      setKey(key + 1);
      setCurrentPosition(position);
  };

  const handleClickPrev = () => {
    let position = currentPosition + imageWidth * 1;
    // if (!infinite) {
    //   position = 0;
    // }

    if (infinite) {
      //setImages([...images.slice(-1), ...images])
      images.unshift(...images.splice(-1))
    }
    setCurrentPosition(position);
  }

  return (
    <div className="HomePage">
      <div className="ProductsSlider">
        <button
          className="ProductsSlider-Button"
          onClick={handleClickPrev}
        >
          <img src="./img/prev.svg" alt="Prev"/>
        </button>
        <div className="ProductsSlider-Container">
          <ul
            className="ProductsSlider-List"
            style={{
              transition: `transform ${1000}ms`,
              transform: `translateX(${currentPosition}px)`,
              // marginLeft: `${0}px`,
              //  marginLeft: `${currentPosition}px`,
            //    marginLeft: `${infinite ?
            //     -(currentPosition+imageWidth * 1)
            //     : 0}px`,
            }}
          >
            {images.map((image) => (
              <li
                className="ProductsSlider-Item"
                // key={key + index}
                key={image}
                // style={{
                //   transition: `transform ${1000}ms`,
                //   transform: `translateX(${currentPosition}px)`,
                  // marginLeft: `${0}px`,
                  // marginLeft: `${currentPosition}px`,
              //    marginLeft: `${infinite ?
              //      -(currentPosition+imageWidth * 1)
              //      : 0}px`,
              //  }}
              >
                <img
                  src={image}
                  alt={image}
                  className="image"
                  // style={{
                  //   transition: `transform ${300}ms`,
                  //   transform: `translateX(${currentPosition}px)`,
                  //   marginLeft: `${0}px`,
                  // }}
                  // // style={{
                  // //   width: `${imageWidth}px`,
                  // // }}
                />
              </li>
            ))}
          </ul>
        </div>

        <button className="ProductsSlider-Button" onClick={() => handleClickNext()}>
          <img src="./img/next.svg" alt="Next"/>
        </button>
      </div>

    </div>
  )
}
