import React, { useEffect, useState } from 'react';
import { useCallback } from 'react';
import './HomePage.scss';

const initialImages = [
  './img/slider/1.jpg',
  './img/slider/2.jpg',
  './img/slider/3.jpg',
  './img/slider/4.jpg',
  './img/slider/5.jpg',
  './img/slider/6.jpg',
];


export const HomePage = () => {
  const [images, setImages] = useState(initialImages);
  //const [imageStart, setImageStart] = useState(initialImages);
  const [ imageWidth, setImageWidth] = useState(Math.ceil(document.documentElement.clientWidth * 72.2 / 100));
  const [ currentPosition, setCurrentPosition] = useState(0);
  // useEffect(() => {
  //   setImages(initialImages);
  // }, [])

  console.log(imageWidth);
  const handleClickNext = useCallback(() => {
    let position = currentPosition - imageWidth;
    images.push(...images.splice(0, 1));
      setImages([...images.slice(1), ...images.slice(0, 1)]);
      console.log(images);
      // setImages([...images, im[0]]);
      setCurrentPosition(position);
  }, [images]);

  return (
    <div className="HomePage">
      <div className="ProductsSlider">
        <button className="ProductsSlider-Button">
          <img src="./img/prev.svg" alt="Prev"/>
        </button>
        <ul
          className="ProductsSlider-List"
          // style={{
          //   transition: `transform ${300}ms`,
          //   transform: `translateX(${currentPosition}px)`,
          //   marginLeft: `${0}px`,
          // }}
        >
          {images.map(image => (
            <li
              className="ProductsSlider-Item"
              key={Math.random()}
              style={{
                transition: `transform ${300}ms`,
                transform: `translateX(${currentPosition}px)`,
                marginLeft: `${0}px`,
              }}
            >
              <img
                src={image}
                alt={image}
                className="image"
                style={{
                  transition: `transform ${300}ms`,
                  transform: `translateX(${currentPosition}px)`,
                  marginLeft: `${0}px`,
                }}
                // style={{
                //   width: `${imageWidth}px`,
                // }}
              />
            </li>
          ))}
        </ul>
        <button className="ProductsSlider-Button" onClick={handleClickNext}>
          <img src="./img/next.svg" alt="Next"/>
        </button>
      </div>

    </div>
  )
}
