import React, {useEffect, useState, useMemo } from 'react';
import './ProductDetailsPage.scss';
import { useHistory, useParams } from 'react-router';
import { NavLink } from 'react-router-dom';
import products from '../../data/products.json';
import techProducts from '../../data/techProducts.json';
import classNames from 'classnames';
//import { useContext } from 'react';
//import { ProductsContext } from '../../ProductsProvider';

export const ProductDetailsPage = () => {
  //const { path } = useContext(ProductsContext);
  const [product, setProduct] = useState({});
  const [image, setImage] = useState('');
  const [imageHover, setImageHover] = useState('');
  const [colorList, setColorList] = useState([[]]);
  const history = useHistory();
  const { productId, type } = useParams() || '0';
  console.log(productId, type);
  const { name, images = [], model = '' } = product;

  useEffect(() => {
    setProduct(products.find(item => item.id === +productId));
    setColorList(techProducts.find(item => item.model === product.model));
    // setColors(tech.find(item => item.model === model).colors);
  }, [productId]);

  useEffect(() => {
    setImage(images[0]);
   // setColorList(techProducts.find(item => item.model === product.model));
  }, [product]);

  useMemo(() => {
    if (product) {

    }
   setColorList(techProducts.find(item => item.model === product.model));
  }, [product]);

  console.log(product, products.images);
  console.log(colorList);
  console.log(techProducts);

  return (
    <div className="ProductDetailsPage">
      <div className="ProductDetailsPage-Breadcrumbs Breadcrumbs">
        <div className="Breadcrumbs-List">
          <NavLink className="Breadcrumbs-Item" to="/home">
            <img src="./img/home.svg" alt="Home"/>
          </NavLink>
          <img src="./img/next-disabled.svg" alt="Next" className="Breadcrumbs-Item"/>
          <NavLink className="Breadcrumbs-Item text" to={`/${type}`}>
            {type[0].toUpperCase() + type.slice(1)}
          </NavLink>
          <img src="./img/next-disabled.svg" alt="Next" className="Breadcrumbs-Item"/>
          <p className="Breadcrumbs-Item text">{name}</p>
        </div>
        <div className="Breadcrumbs-Back">
          <img src="./img/prev.svg" alt="Prev"/>
          <button
            className="Breadcrumbs-Button"
            type="button"
            onClick={() => history.goBack(-1)}
          >
            Back
          </button>
        </div>
      </div>
      <h1 className="ProductDetailsPage-Title">{name}</h1>
      <div className="ProductDetailsPage-ContainerTop">
        <div className="ProductDetailsPage-ImagesBlock ImagesBlock">
          <ul className="ImagesBlock-List">
            {images.map(item => (
              <li
                key={item}
                className={classNames('ImagesBlock-Item', {
                  item_active: item === image,
                })}
                onClick={() => setImage(item)}
                onMouseEnter={() => setImageHover(item)}
                onMouseLeave={() => setImageHover('')}
              >
                <img
                  src={item}
                  alt="Product"
                  width="64px"
                  height="64px"
                  className="itemImage"
                />
              </li>
            ))}
          </ul>
          <img
            src={imageHover ? imageHover : image}
            alt="Product"
            width="200px"
            height="200px"
            className="ImagesBlock-Image"
          />
        </div>
        <div className="ProductDetailsPage-OptionsBlock">
          <div className="ProductDetailsPage-Colors Colors">
            <h3 className="Colors-Title">Available colors</h3>
            <ul className="Colors-List">
              {colorList.colors}
            </ul>
          </div>
        </div>
      </div>
    </div>
  )
}
