import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import './Search.scss';

export const Search = () => {
  const { pathname } = useLocation();
  console.log(pathname)
  const [value, setValue] = useState('');

  return (
    <div className="Search" tabindex="0">
      <input
        className="Search-Input input"
        type="text"
        name="search"
        placeholder={`Search in ${pathname.slice(1)}...`}
        value={value}
        onChange={e => setValue(e.target.value)}
      />
      {value ? (
        <button
        className="Search-ButtonClear button input"
        type="button"
        onClick={() => {}}
      >
        <img
          src="./img/clear.svg"
          alt="Clear"
        />
      </button> 

      ) : (
      <button
        className="Search-ButtonSearch button in"
        type="button"
        tabindex="0"
        onClick={() => {}}
      >
        <img
          src="./img/search.svg"
          alt="Search"
        />
      </button>
      )}
      {/* <button
        className="Search-ButtonClear button"
        type="button"
        onClick={() => {}}
      >
        <img
          src="./img/clear.svg"
          alt="Clear"
        />
      </button> */}


    </div>
  )
}
