import React, { useContext } from 'react';
import { useLocation } from 'react-router-dom';
import { ProductsContext } from '../../ProductsProvider';
import { NavLink } from 'react-router-dom';
import { CartProduct } from '../CartProduct';
import './FavoritesPage.scss';

export const FavoritesPage = () => {
  const { pathname } = useLocation();
  const { favorites, productsList, appliedValue } = useContext(ProductsContext);
  const getProductList = () => (
    appliedValue[pathname.slice(1)]
             ?  productsList
                  .filter(product => favorites.includes(product.id))
                  .filter(product => product.name.toLowerCase().includes(appliedValue[pathname.slice(1)].toLowerCase()))
             :  productsList.filter(product => favorites.includes(product.id))
  )

  return (
    <div className="FavoritesPage">
      {!appliedValue[pathname.slice(1)] && (
        <div className="FavoritesPage-Nav">
          <NavLink className="FavoritesPage-Link nav" to="/home">
            <img src="./img/home.svg" alt="Home" className="nav"/>
          </NavLink>
          <img src="./img/next-disabled.svg"/>
          <p className="nav">Favorites</p>
        </div>
      )}
      <div className="FavoritesPage-Main">
        {!appliedValue[pathname.slice(1)] && (
          <>
            <h1 className="FavoritesPage-Title">Favorites</h1>
            <p className="FavoritesPage-Count">
            {favorites.length} items
            </p>
          </>
        )}
        {appliedValue[pathname.slice(1)] && (
          <p className="FavoritesPage-Results">
          {getProductList().length} results
          </p>
        )}
        <p className="FavoritesPage-Count">
          {appliedValue[pathname.slice(1)] ? getProductList().length : favorites.length} items 
        </p>
        <div className="FavoritesPage-Content">
          { getProductList()
           .map(product => <CartProduct {...product} />)
          }
        </div>

      </div>
    </div>
  )
}
