import React, { useMemo, useState, useEffect, useCallback } from 'react';
import { useLocation } from 'react-router-dom';
import { PropTypes } from 'prop-types';
import products from './data/products.json';
import { debounce } from './helpers/debounce';
import { initialValue } from './helpers/search';


export const ProductsContext = React.createContext({
  favourites: [],
  setFavorites: () => {},
  carts: [],
  setCarts: () => {},
  productsList: [],
  appliedValue: {},
  setAppliedValue: () => {},
  applyValue: () => {},
  path: '',

});

export const ProductsProvider = ({ children }) => {
  const [productsList, setProductsList] = useState(products);
  const [appliedValue, setAppliedValue] = useState(initialValue);
  const applyValue = useCallback(debounce(setAppliedValue, 1000), []);

  const { pathname } = useLocation();
  const path = useMemo(() => pathname.slice(1), [pathname]);
  console.log(path);

  const [favorites, setFavorites] = useState(
    JSON.parse(localStorage.getItem('favorites')) || []
  );
  const [carts, setCarts] = useState(JSON.parse(localStorage.getItem('carts')) || []);


  console.log(favorites.filter);
  console.log(products.filter(item => favorites.includes(item.id)));

  useEffect(() => (
    localStorage.setItem('favorites', JSON.stringify(favorites))
  ), [favorites]);

  useEffect(() => (
    localStorage.setItem('carts', JSON.stringify(carts))
  ), [carts]);

  const contextValue = useMemo(() => (
    {
      favorites,
      setFavorites,
      carts,
      setCarts,
      productsList,
      appliedValue,
      setAppliedValue,
      applyValue,
      path,
    }
  ), [favourites, carts, productsList, appliedValue,]);


  return (
    <ProductsContext.Provider value={contextValue}>
      {children}
    </ProductsContext.Provider>
  )
}

ProductsProvider.propTypes = {
  children: PropTypes.array.isRequired,
};
