import React, { useContext} from 'react';
import { ProductsContext } from '../../ProductsProvider';
import { PageContent } from '../PageContent';
import { Route} from 'react-router-dom';
import { ProductDetailsPage } from '../ProductDetailsPage';
import './PhonesPage.scss';

export const PhonesPage = () => {
  const { productsList, path, productType } = useContext(ProductsContext);

  return (
    <>
    <Switch>
      <Route path="/phones/product"><ProductDetailsPage /></Route>
      <Route path="/phones">
        <PageContent title="Mobile phones" products={productsList[path]} />
      </Route>
    </Switch>
    <Route path="/phones/product"><ProductDetailsPage /></Route>
    {/* { path.includes('phones/product') ?
    <Route path={`/${path}/:productId?`} component={ProductDetailsPage} />: */}
    <PageContent title="Mobile phones" products={productsList[path]} />
  {/* } */}
  </>
  )
}
