export const getSliderWidth = () => (
  (window.innerWidth > 655) 
    ? Math.ceil((window.innerWidth * 72.2 / 100) + 96)
    : Math.ceil(window.innerWidth - 48)
)

const getSliderCartLength = () => {
  let cartWidth = 272;
  let width = getSliderWidth() ;
  return Math.trunc(width / cartWidth);
}
