export const getSliderWidth = () => (
  (window.innerWidth > 655) 
    ? Math.ceil((window.innerWidth * 72.2 / 100) + 96)
    : Math.ceil(window.innerWidth - 48)
)

export const getSliderStep = (sliderWidth) => {
  const cartWidth = 272;
  // const sliderWidth = getSliderWidth() ;
  return Math.trunc(sliderWidth / cartWidth);
}

 export const getCartMarginRight = (sliderWidth, sliderStep) => {
  const cartWidth = 272;
  //let width = getSliderWidth();
  //let cartLength = getCartLength();
  return Math.trunc((width - (cartWidth * sliderStep)) / (sliderStep - 1))
}
