export const initialValue = {
  phones: '',
  tablets: '',
  accessories: '',
  favorites: '',
}

export const setValueInput = (pathname, value) => {
  switch(pathname.slice(1)) {
    case 'phones':
      return value.phones;
    case 'tablets':
      return value.tablets;
    case 'accessories':
      return value.accessories;
    case 'favorites':
      return value.favorites;
    default:
      break;
  }
}
