import React, { useContext } from 'react';
import classNames from 'classnames';
import PropTypes from 'prop-types';
import './SelectSort.scss';
import { ProductsContext } from '../../ProductsProvider';

export const SelectSort = ({ list }) => {

  const { path, setPage, page } = useContext(ProductsContext);

  return (
    <div className="SelectCarts">
      <p className="SelectCarts-Text">{option[path]}</p>
      {!toggle[path] ? (
        <button
          className="SelectCarts-Button"
          type="button"
          onClick={() => {
            setToggle({...toggle, [path]: true});
            console.log('kat');
          }}
        >
          <img src="./img/down.svg" alt="Down"/>
        </button>
      ) : (
        <button
          className="SelectCarts-Button"
          type="button"
          onClick={() => setToggle({...toggle, [path]: false})}
        >
          <img src="./img/up.svg" alt="Up"/>
        </button>
      )}
      <ul className={classNames('SelectCarts-List', {
          visible_list: !toggle[path],
        })}
      >
        {list.map(item => (
          <li
            className={classNames('SelectCarts-Item', {
              selected: item === option[path],
            })}
            key={item}
            onClick={() => setOption({...option, [path]: item})}
          >
            {item}
          </li>
        ))}
      </ul>
    </div>
  );
}

SelectSort.propTypes = {
  list: PropTypes.array.isRequired,
}
