import React, { useContext, useMemo, useEffect } from 'react';
import { ProductsContext } from '../../ProductsProvider';
import PropTypes from 'prop-types';
import { NavLink } from 'react-router-dom';
import { CartProduct } from '../CartProduct';
import { SelectSort } from '../SelectSort';
import { SelectItems } from '../SelectItems';
import './PageContent.scss';
import { Pagination } from '../Pagination';

export const PageContent = ({ products, title }) => {
  const {
    appliedValue,
    path,
    sortCarts,
    pageItems,
    page,
    changeHistory,
    deleteHistory,
  } = useContext(ProductsContext);

  useEffect(() => {
    if (path !== 'favorites') {
      if (pageItems[path] !== "All items") {

        changeHistory('page', page[path]);
        changeHistory('perPage', pageItems[path]);

      } else if (pageItems[path] === "All items") {
        deleteHistory('page');
        deleteHistory('perPage');
      }
    }
  }, [page[path], pageItems[path]])

  let forPage = ((page[path] - 1) * pageItems[path]);
  let toPage = pageItems[path] * page[path];

  const sortProducts = useMemo(() => {
    switch(sortCarts[path]) {
      case 'Alphabetically':
        return [...products].sort((a, b) => (
          a.name.localeCompare(b.name)));
      case 'Price low to hight':
        return [...products].sort((a, b) => (
          a.price[0] - b.price[0]));
      case 'Price hight to low':
        return [...products].sort((a, b) => (
          b.price[0] - a.price[0]));
      default:
        return products;
    }
  }, [sortCarts[path], products]);

  const visibleProducts = useMemo(() => {
    if ( appliedValue[path]) {
      return products.filter(product => product.name.toLowerCase()
      .includes(appliedValue[path].toLowerCase()))
    }

    if (!appliedValue[path]) {
      if (pageItems[path] !== "All items") {
        return sortProducts.slice(forPage, toPage);
      }
      return sortProducts;
    }
  }, [appliedValue[path],
     sortProducts,
     products,
     pageItems[path],
     forPage,
     toPage]);

  return (
    <div className="PageContent">
      {!appliedValue[path] && (
        <div className="PageContent-Nav">
          <NavLink className="PageContent-Link nav" to="/home">
            <img src="./img/home.svg" alt="Home" className="nav"/>
          </NavLink>
          <img src="./img/next-disabled.svg" alt="Next" className="nav"/>
          <p className="nav">{path[0].toUpperCase() + path.slice(1)}</p>
        </div>
      )}
      <div className="PageContent-Main">
        {!appliedValue[path] && (
          <>
            <h1 className="PageContent-Title">{title}</h1>
            <p className="PageContent-Count">
            {visibleProducts.length} items
            </p>
          </>
        )}
        {appliedValue[path] && (
          <p className="PageContent-Results">
          {visibleProducts.length} results
          </p>
        )}
        {(path !== 'favorites' && !appliedValue[path]) && (
          <div className="PageContent-Selects">
            <div className="PageContent-SelectsContainer">
              <p className="PageContent-SelectName">Sort by</p>
              <div className="PageContent-SelectSort">
                <SelectSort list={['Alphabetically', 'Price hight to low', 'Price low to hight']}/>
              </div>
            </div>
            <div className="PageContent-SelectsContainer PageContent-SelectsContainer_hidden">
              <p className="PageContent-SelectName">Items on page</p>
              <div className="PageContent-SelectPageItems">
                <SelectItems list={['All items', '7', '14']}/>
              </div>
            </div>
          </div>
        )}
        <div className="PageContent-Content">
          { visibleProducts.map(product => <CartProduct {...product} key={product.id}/>)}
        </div>
      </div>
      {((path !== 'favorites' && pageItems[path] !== "All items")) && <Pagination total={sortProducts.length}/>}
    </div>
  )
}

PageContent.propTypes = {
  products: PropTypes.array.isRequired,
  title: PropTypes.string.isRequired,
}
