import React, { useContext } from 'react';
import classNames from 'classnames';
import PropTypes from 'prop-types';
import './SelectSort.scss';
import { ProductsContext } from '../../ProductsProvider';

export const SelectSort = ({ list }) => {
  const {
    path,
    toggleSort,
    setToggleSort,
    sortCarts,
    setSortCarts
  } = useContext(ProductsContext);

  return (
    <div className="Select">
      <button
        className={classNames('Select-Button', {
          image_rotate: toggleSort[path]
        })}
        type="button"
        onClick={() => {
          setToggleSort({...toggleSort, [path]: true});
        }}
      >
        <p className="Select-Text">{sortCarts[path]}</p>
        <img src="./img/down.svg" alt="Down"/>
      </button>



