import React, { useContext, useMemo, useState } from 'react';
import { ProductsContext } from '../../ProductsProvider';
import PropTypes from 'prop-types';
import { NavLink } from 'react-router-dom';
import { CartProduct } from '../CartProduct';
import { SelectCarts } from '../SelectCarts';
import './PageContent.scss';
import { initialSortSelect, initialToggleSelect, initialItemsPageSelect } from '../../helpers/select';
//import classNames from 'classnames';

export const PageContent = ({ products, title }) => {
  const {
    appliedValue,
    path,
    sortCarts,
    setSortCarts,
    toggleSort,
    setToggleSort,
    pageItems,
    setPageItems,
    toggleItemsPage,
    setToggleItemsPage,
  } = useContext(ProductsContext);

  // console.log(sortCarts);
  // console.log(toggleSort);
  // console.log(pageItems);
  // console.log(toggleItemsPage);
  const sortProducts = () => {
    // let products;
    switch(sortCarts[path]) {
      case 'Alphabetically':
        return [...products].sort((a, b) => (
          a.name.localeCompare(b.name)))

    }
  }
  const visibleProducts = useMemo(() => (
    appliedValue[path]
      ?  products.filter(product => product.name.toLowerCase()
          .includes(appliedValue[path].toLowerCase()))
      :  products
  ), [appliedValue[path], products]);

  return (
    <div className="PageContent">
      {!appliedValue[path] && (
        <div className="PageContent-Nav">
          <NavLink className="PageContent-Link nav" to="/home">
            <img src="./img/home.svg" alt="Home" className="nav"/>
          </NavLink>
          <img src="./img/next-disabled.svg" alt="Next" className="nav"/>
          <p className="nav">{path[0].toUpperCase() + path.slice(1)}</p>
        </div>
      )}
      <div className="PageContent-Main">
        {!appliedValue[path] && (
          <>
            <h1 className="PageContent-Title">{title}</h1>
            <p className="PageContent-Count">
            {visibleProducts.length} items
            </p>
          </>
        )}
        {appliedValue[path] && (
          <p className="PageContent-Results">
          {visibleProducts.length} results
          </p>
        )}
        {(path !== 'favorites' && !appliedValue[path]) && (
          <div className="PageContent-Selects">
            <div className="PageContent-SelectsContainer">
              <p className="PageContent-SelectName">Sort by</p>
              <div className="PageContent-SelectSort">
                <SelectCarts
                  toggle={toggleSort}
                  setToggle={setToggleSort}
                  option={sortCarts}
                  setOption={setSortCarts}
                  list={['Alphabetically', 'Price hight to low', 'Price low to hight']}
                />
              </div>
            </div>
            <div className="PageContent-SelectsContainer PageContent-SelectsContainer_hidden">
              <p className="PageContent-SelectName">Items on page</p>
              <div className="PageContent-SelectPageItems">
                <SelectCarts
                  toggle={toggleItemsPage}
                  setToggle={setToggleItemsPage}
                  option={pageItems}
                  setOption={setPageItems}
                  list={['All items', '7', '14']}
                />
              </div>
            </div>
          </div>
        )}
        <div className="PageContent-SelectItemsPage">{}</div>
      </div>
      <div className="PageContent-Content">
        { visibleProducts.map(product => <CartProduct {...product} key={product.id}/>)}
      </div>
    </div>
  )
}

PageContent.propTypes = {
  products: PropTypes.array.isRequired,
  title: PropTypes.string.isRequired,
}
