import { createSecretKey } from 'crypto';
import React, { useEffect, useState } from 'react';
import { useCallback } from 'react';
import classNames from 'classnames';
import './HomePage.scss';

const initialImages = [
  './img/slider/1.jpg',
  './img/slider/2.jpg',
  './img/slider/3.jpg',
  './img/slider/4.jpg',
  './img/slider/5.jpg',
  './img/slider/6.jpg',
];

export const HomePage = () => {
  const [images, setImages] = useState([...initialImages]);
  const [imageWidth, setImageWidth] = useState(document.documentElement.clientWidth * 72.2 / 100);
  const [currentPosition, setCurrentPosition] = useState(0);
  // const [currentImage, setCurrentImage] = useState(initialImages[0])
  const [currentImage, setCurrentImage] = useState(0);
  // useState(...images.slice(0, 1));

  // useEffect(() => {
  //   setInterval(() => handleClickNext(), 5000)
  // },[])

  console.log(imageWidth);
  console.log(currentImage);
  const handleClickNext = () => {
    let position = currentPosition - imageWidth * 1;
    setImages([...images.slice(1), ...images.splice(0, 1)]);
    setCurrentPosition(position);
    setCurrentImage(currentImage < 5 ? currentImage + 1 : 0);
  };

  useEffect(() => {
    const intervalId = setInterval(() => {
      // setCurrentImage(currentImage < 5 ? currentImage + 1 : 0);
      // setImages([...images.slice(1), ...images.splice(0, 1)]);
      // setCurrentPosition(currentPosition - imageWidth * 1);
      // setCurrentImage(currentImage < 5 ? currentImage + 1 : 0);
      handleClickNext();
      ;}, )
      return () => {
        clearInterval(intervalId);
      }
  })

  const handleClickPrev = () => {
    let position = currentPosition + imageWidth * 1;
    setImages([...images.splice(-1), ...images]);
    setCurrentPosition(position);
    setCurrentImage(currentImage === 0 ? 5 : currentImage - 1);
  }

  return (
    <div className="HomePage">
      <div className="ProductsSlider">
        <button
          className="ProductsSlider-Button"
          onClick={handleClickPrev}
        >
          <img src="./img/prev.svg" alt="Prev"/>
        </button>
        <div className="ProductsSlider-Container">
          <ul
            className="ProductsSlider-List"
            style={{
              transition: `transform ${500}ms`,
              transform: `translateX(${currentPosition}px)`,
              marginLeft: `${currentPosition ?
                -(currentPosition+imageWidth * 1)
                : 0}px`,
            }}
          >
            {images.map((image) => (
              <li
                className="ProductsSlider-Item"
                key={image}
              >
                <img
                  src={image}
                  alt={image}
                  className="image"
                />
              </li>
            ))}
          </ul>
        </div>
        <button className="ProductsSlider-Button" onClick={handleClickNext}>
          <img src="./img/next.svg" alt="Next"/>
        </button>
      </div>
      <div className="ProductsSlider-BlockButtons">
        {[...initialImages].map((item, index) => (
          <button
            key={index + 1}
            type="button"
            className={classNames("ProductsSlider-SliderButton", {
              button_active: index === currentImage,
            })}
            onClick={() => console.log(item)}
          >
            {}
          </button>
        ))}
      </div>
    </div>
  )
}
