import React, { useContext } from 'react';
import './CartProduct.scss';
import { ProductsContext } from '../../ProductsProvider';

export const CartProduct = ({
  sliderWidth,
  margin,
  marginLeft,
  carts,
}) => {

  const { favorites, setFavorites } = useContext(ProductsContext);

  return (
    <>
      {carts.map(({ images, name, price, capacity, id}) => (
        <div
          className="CartProduct"
          key={name}
          style={{
            marginRight: `${sliderWidth < 555 ? 0 : margin}px`,
            marginLeft: `${sliderWidth < 555 ? marginLeft / 2 : 0}px`
          }}
        >
          <div className="CartProduct-ImageContainer">
            <img
              className="CartProduct-Image"
              src={images[0]}
              alt="Iphone"
              width="190px"
            />
          </div>
          <div className="CartProduct-Info">
            <h3 className="CartProduct-Name">
              {name}
            </h3>
            <div className="CartProduct-Price">
              <div className="price">{`${price[0]}$`}</div>
              <div className="sale">
                {price[1] ? `${price[1]}$` : ''}
              </div>
            </div>
            <div className="line">{}</div>
            <div className="CartProduct-Description">
              <div className="CartProduct-Item">
                <p className="CartProduct-Screen name">Screen</p>
                <p className="CartProduct-ScreenIndex index">6.5” OLED</p>
              </div>
              <div className="CartProduct-Item">
                <p className="CartProduct-Capacity name">Capacity</p>
                <p className="CartProduct-CapacityRam index">{capacity}</p>
              </div>
              <div className="CartProduct-Item">
                <p className="CartProduct-Ram name">RAM</p>
                <p className="CartProduct-RamRam index">4 GB</p>
              </div>
            </div>
          </div>
          <div className="CartProduct-Buttons">
            {!carts.includes(id) ? (
              <button
                type="button"
                className="CartProduct-ButtonAdd"
                onClick={() => {}}
              >
                Add to cart
              </button>
            ) : (
                <button
                type="button"
                className="CartProduct-ButtonAdd"
                onClick={() => {}}
              >
                Add to cart
              </button>
            )}
            <button
              type="button"
              className="CartProduct-ButtonAdd"
              onClick={() => {}}
            >
              Add to cart
            </button>
            {!favorites.includes(id) ? (
              <button
              type="button"
              className="CartProduct-ButtonFavourites"
              onClick={() => setFavorites([ ...favorites, id ])}
            >
              <img src="./img/favourites.svg" alt="Favourites"/>
            </button>
            ) : (
              <button
                type="button"
                className="CartProduct-ButtonFavourites"
                onClick={() => setFavorites(favorites.filter(item => item != id))}
              >
                <img src="./img/favourites-selected.svg" alt="Favourites"/>
              </button>
            )}
          </div>
        </div>
      ))}
    </>
  );
}
