import React , { useState, useEffect } from 'react';
import { getSliderWidth , getCartLength, getMargin, getMarginLeft} from '../../helpers/slider';
import classNames from 'classnames';
import { CartProduct } from '../CartProduct';
import cartProducts from '../../data/phones.json';
import './CartSlider.scss';


export const CartSlider = () => {
  const [carts, setCarts] = useState(cartProducts)
  const [sliderWidth, setSliderWidth] = useState(getSliderWidth())
  const [currentPosition, setCurrentPosition] = useState(0);
  const [cartLength, setCartLength] = useState(getCartLength());
  const [margin, setMargin] = useState(getMargin());
  const [marginLeft, setMarginLeft] = useState(getMarginLeft());
  const [buttonNext, setButtonNext] = useState(1);
  const [buttonPrev, setButtonPrev] = useState('');

  const handleResize = () => {
      setSliderWidth(getSliderWidth());
      setCartLength(getCartLength());
      setMargin(getMargin());
      setMarginLeft(getMarginLeft)
    }



  const handleClickPrev = () => {
    window.addEventListener('resize', handleResize);
    //let cartLength = getCartLength();

    let position = currentPosition + (cartLength ===1 ? sliderWidth - (marginLeft / 2) : sliderWidth + margin);

    if (position > 0) {
      position = 0;
      setButtonPrev("");
   }

   if (position < 0) {
      setButtonNext(1);
   }
    setCurrentPosition(position);
  }

  const handleClickNext = () => {
    window.addEventListener('resize', handleResize);
    //let cartLength = getCartLength();
    let position = currentPosition - (cartLength === 1 ?
      (sliderWidth - (marginLeft / 2)) : (sliderWidth + margin));
    let maxPosition = (((carts.length - cartLength) / cartLength) * (cartLength === 1 ? sliderWidth - marginLeft / 2 : sliderWidth + margin));

    if (-(position) > maxPosition) {
      position = -maxPosition;
      setButtonNext('')
    }

    if (-(position) < maxPosition) {
      setButtonPrev(1)
    }

    setCurrentPosition(position);
  };

  console.log(currentPosition, buttonPrev, buttonNext);
  return (
    <>
      <div className="CartSlider">
        <div className="CartSlider-Container">
          <p className="CartSlider-Title">Title</p>
          <div className="CartSlider-Buttons">
            <button
              className={classNames('button_cart', {
              //prev_disabled: !buttonPrev,
              //button_prev: buttonPrev,
              })}
              onClick={handleClickPrev}
            >
              {}
            </button>
            <button
              className={classNames('button_cart', {
              //next_disabled: !buttonNext,
             // button_next: buttonNext,
              })}
              onClick={handleClickNext}
            >
              {}
            </button>
          </div>
        </div>
        <div className="CartSlider-Slider">
          <div
            className="CartSlider-Content"
            style={{
              transition: `transform ${1000}ms`,
              transform: `translateX(${currentPosition}px)`,
              marginLeft: `-${(currentPosition + sliderWidth)}`
            }}
          >
          {/* {carts.map(item => ( */}
            {/* <div
              key={item}
              className="Cart"
              style={{
                marginRight: `${sliderWidth < 655 ? 0 : margin}px`,
                marginLeft: `${sliderWidth < 655 ? marginLeft / 2 : 0}px`
            }}
              > */}
              {/* <img src={`./img/slider/1.jpg`} className="Cart-Image"/> */}
              <CartProduct
                sliderWidth={sliderWidth}
                margin={margin}
                marginLeft={marginLeft}
              />
            {/* </div> */}
          {/* //))} */}
          </div>
        </div>
      </div>
      {/* <CartProduct /> */}
    </>
  )
}
