import React, { useContext } from 'react';
import classNames from 'classnames';
import PropTypes from 'prop-types';
import { ProductsContext } from '../../ProductsProvider';

export const SelectItems = ({ list }) => {
  const {
    path,
    setPage,
    page,
    toggleItemsPage,
    setToggleItemsPage,
    pageItems,
    setPageItems
  } = useContext(ProductsContext);

  useMemo(() => {
    if (pageItems[path] !== "All items" &&
      !['home', 'favorites', 'cart'].includes(path) ) {
      searchParams.set('page', page[path]);
      history.push(`?${searchParams.toString()}`);
    } else if (pageItems[path] === "All items") {
      searchParams.delete('page');
      history.push(`?${searchParams.toString()}`);
    }
  },[pageItems[path]]);

  return (
    <div className="Select">
      <p className="Select-Text">{pageItems[path]}</p>
      {!toggleItemsPage[path] ? (
        <button
          className="Select-Button"
          type="button"
          onClick={() => setToggleItemsPage({...toggleItemsPage, [path]: true})}
        >
          <img src="./img/down.svg" alt="Down"/>
        </button>
      ) : (
        <button
          className="Select-Button"
          type="button"
          onClick={() => setToggleItemsPage({...toggleItemsPage, [path]: false})}
        >
          <img src="./img/up.svg" alt="Up"/>
        </button>
      )}
      <ul className={classNames('Select-List', {
          visible_list: !toggleItemsPage[path],
        })}
      >
        {list.map(item => (
          <li
            className={classNames('Select-Item', {
              selected: item === pageItems[path],
            })}
            key={item}
            onClick={() => {
              setPageItems({...pageItems, [path]: item});
              if(item !== pageItems[page]) {
                setPage({...page, [path]: 1});
              }
            }}
          >
            {item}
          </li>
        ))}
      </ul>
    </div>
  );
}

SelectItems.propTypes = {
  list: PropTypes.array.isRequired,
}
