import React, { useContext, useState, useEffect } from 'react';
import classNames from 'classnames';
import PropTypes from 'prop-types';
import './SelectCarts.scss';
import { ProductsContext } from '../../ProductsProvider';
import { initialSortSelect, initialToggleSelect, initialItemsPageSelect } from '../../helpers/select';

export const SelectCarts = ({
  // toggleSort,
  // setToggleSort,
  // sortCarts,
  //setSortCarts,
  list,
}) => {
  // const [sortCarts, setSortCarts] = useState(initialSortSelect)
  // const [toggleSort, setToggleSort] = useState(initialToggleSelect);

  // useEffect(() => {
  //   setSortCarts(initialSortSelect);
  //   setToggleSort(initialToggleSelect);
  // }, []);

  // const [pageItems, setPageItems] = useState(initialItemsPageSelect)
  // const [toggleItemsPage, setToggleItemsPage] = useState(initialToggleSelect);

  const {
    path,
    sortCarts,
    setSortCarts,
    toggleSort,
    setToggleSort
  } = useContext(ProductsContext);
  console.log(path);
  console.log(toggleSort, sortCarts);

  return (
    <div className="SelectCarts">
      {/* <div className="SelectCarts-Container"> */}
        <p className="SelectCarts-Text">{sortCarts[path]}</p>
        {!toggleSort[path] ? (
          <button
            className="SelectCarts-Button"
            type="button"
            onClick={() => {
              setToggleSort({...toggleSort, [path]: true});
              console.log('kat');
            }}
          >
            <img src="./img/down.svg" alt="Down"/>
          </button>
        ) : (
          <button
            className="SelectCarts-Button"
            type="button"
            onClick={() => setToggleSort({...toggleSort, [path]: false })}
          >
            <img src="./img/up.svg" alt="Up"/>
          </button>
        )}
      {/* //</div> */}
      <ul className={classNames('SelectCarts-List', {
          visible_list: !toggleSort[path],
        })}
      >
        {list.map(item => (
          <li
            className={classNames('SelectCarts-Item', {
              selected: item === sortCarts[path],
            })}
            key={item}
            onClick={() => setSortCarts({...sortCarts, [path]: item})}
          >
            {item}
          </li>
        ))}
      </ul>
    </div>
  );
}

SelectCarts.propTypes = {
  // toggleSort: PropTypes.object.isRequired,
  // setToggleSort: PropTypes.func.isRequired,
  // sortCarts: PropTypes.object.isRequired,
  // setSortCarts: PropTypes.func.isRequired,
  list: PropTypes.array.isRequired,
}
