import React, { useState } from 'react';
import PropTypes from 'prop-types';
import './ProductCart.scss';
import phones from '../../data/phones.json';

export const ProductCart = ({ name, price, images, id, quantity, setQuantity }) => {
  //const [quantity, setQuantity] = useState(1);
  // const [quantity, setQuantity] = useState(
  //   JSON.parse(localStorage.getItem('quantity')) || {[id]: 1}
  // );
  useEffect(() => setQuantity({...quantity, [id]: 1}))

  return (
    <div className="ProductCart">
      <div className="ProductCart-ContainerLeft">
        <button className="ProductCart-Close">{}</button>
        <img
          className="ProductCart-Image"
          src={images[0]}
          width="66px"
          height="66px"
          alt="Phone"
        />
        <p className="ProductCart-Name">{name}</p>
      </div>
      <div className="ProductCart-ContainerRight">
        <div className="ProductCart-ContainerQuantity">
          <button className="ProductCart-Remove buttonProduct" disabled={true}>{}</button>
          <div className="ProductCart-Quantity">{quantity.hasOwnProperty([id]) ? quantity[id] : 1}</div>
          <button
            className="ProductCart-Add buttonProduct"
            onClick={() => {}}
          >{}</button>
        </div>
        <div className="ProductCart-Price">${price[0]}</div>
      </div>
    </div>
  )
}

ProductCart.propTypes = {
  images: PropTypes.arrayOf(PropTypes.string.isRequired,
  ).isRequired,
  name: PropTypes.string.isRequired,
  price: PropTypes.arrayOf(PropTypes.number.isRequired).isRequired,
  id: PropTypes.number.isRequired,
  quantity: PropTypes.object.isRequired,
  setQuantity: PropTypes.func.isRequired,
  // type: PropTypes.string.isRequired,
  // tech: PropTypes.arrayOf(PropTypes.array.isRequired,
  //   ).isRequired,
};
