import React, { useContext, useEffect } from 'react';
import { ProductsContext } from '../../ProductsProvider';
import { useLocation, useHistory } from 'react-router-dom';
import { PageContent } from '../PageContent';
import { PhoneDetails } from '../ProductDetailsPage';
import './PhonesPage.scss';

export const PhonesPage = () => {
  const { productsList, path, changeHistorySort, appliedValue } = useContext(ProductsContext);

  useEffect(() => changeHistorySort(appliedValue), [appliedValue[path], path])
  return (
    <>
      {/* {model ? <PhoneDetails /> : */}
      <PageContent title="Mobile phones" products={productsList[path]} />
    </>
  )
}
