import React, { useContext } from 'react';
import classNames from 'classnames';
import PropTypes from 'prop-types';
import { ProductsContext } from '../../ProductsProvider';

export const SelectItems = ({ list }) => {
  const {
    path,
    setPage,
    page,
    toggleItemsPage,
    setToggleItemsPage,
    pageItems,
    setPageItems,
  } = useContext(ProductsContext);

  return (
    <div className="Select">
      <button
        className={classNames('Select-Button', {
          image_rotate: toggleSort[path],
        })}
        type="button"
        onClick={() => setToggleSort({...toggleSort, [path]: !toggleSort[path]})}
      >
        <p className="Select-Text">{sortCarts[path]}</p>
        <img src="./img/down.svg" alt="Down" className="button-image"/>
      </button>
      {toggleSort[path] && (
        <ul className={classNames('Select-List', {
        })}
        >
          {list.map(item => (
            <li
              className={classNames('Select-Item', {
                selected: item === sortCarts[path],
              })}
              key={item}
              onClick={() => {
                setSortCarts({...sortCarts, [path]: item});
                setToggleSort({...toggleSort, [path]: false});
              }}
            >
              {item}
            </li>
          ))}
        </ul>
      )}


      <p className="Select-Text">{pageItems[path]}</p>
      {!toggleItemsPage[path] ? (
        <button
          className="Select-Button"
          type="button"
          onClick={() => {
            setToggleItemsPage({...toggleItemsPage, [path]: true});

          }}
        >
          <img src="./img/down.svg" alt="Down"/>

        </button>
      ) : (
        <button
          className="Select-Button"
          type="button"
          onClick={() => setToggleItemsPage({...toggleItemsPage, [path]: false})}
        >
          <img src="./img/up.svg" alt="Up"/>
        </button>
      )}
      <ul className={classNames('Select-List', {
          visible_list: !toggleItemsPage[path],
        })}
      >
        {list.map(item => (
          <li
            className={classNames('Select-Item', {
              selected: item === pageItems[path],
            })}
            key={item}
            onClick={() => {
              setPageItems({...pageItems, [path]: item});
              if(item !== pageItems[page]) {
                setPage({...page, [path]: 1});
              }
            }}
          >
            {item}
          </li>
        ))}
      </ul>
    </div>
  );
}

SelectItems.propTypes = {
  list: PropTypes.array.isRequired,
}
