import React from 'react';
import { useLocation } from 'react-router-dom';
//import classNames from 'classnames';
import { ProductsSlider } from '../ProductsSlider';
import { CartSlider } from '../CartSlider';
import './HomePage.scss';
import { Category } from '../Category';
import carts from '../../data/phones.json';

export const HomePage = () => {
  const location = useLocation();
  const { pathname, search } = location;

  return (
    <div className="HomePage" id={pathname}>
      <ProductsSlider />
      <CartSlider title="Hot prices" carts={[...carts].filter(cart => cart.price.length > 1)}/>
      <Category />
      <CartSlider title="Brand new models" carts={[...carts].filter(cart => cart.price.length === 1).sort((a, b) => a.id - b.id)}/>
    </div>
  )
}
