import React, { useContext, useM } from 'react';
import { ProductsContext } from '../../ProductsProvider';
import { useLocation, useHistory } from 'react-router-dom';
import { PageContent } from '../PageContent';
import './PhonesPage.scss';

export const PhonesPage = () => {
  const { productsList, path, search, pageItems, page} = useContext(ProductsContext);
  const location = useLocation();
  const history = useHistory();
  const searchParams = new URLSearchParams(search);

  // const changeHistory = (name, value) => {
  //   searchParams.set(name, value);
  //   history.push(`?${searchParams.toString()}`);
  // };
  useMemo(() => {
    if (pageItems[path] !== "All items") {
      searchParams.set('page', page[path]);
      history.push(`?${searchParams.toString()}`);
    } else {
      searchParams.delete('page');
    }
  }, [pageItems[path], path]);

  return (
    <>
      <PageContent title="Mobile phones" products={productsList[path]} />
    </>
  )
}
