import React, { useContext, useEffect, useMemo, useState } from 'react';
import './CartPage.scss';
import { useHistory } from 'react-router';
import phones from '../../data/phones.json';
import { ProductCart } from '../ProductCart';
import { ProductsContext } from '../../ProductsProvider';

export const CartPage = () => {
  const { carts } = useContext(ProductsContext);
  const [totalPrice, setTotalPrice] = useState();
  const history = useHistory();
  console.log(carts);
  useEffect(() => (
    setTotalPrice(
    phones
      .filter(item => carts.includes(item.id))
      .reduce((count, phone) => count + phone.price[0], 0))
  ), [carts, ]);


  const cartsList = useMemo(() => phones.filter(item => carts.includes(item.id)), [carts]);
  console.log(totalPrice);

  return (
    <div className="CartPage">
      <div className="CartPage-Back Back">
        <img src="./img/prev.svg" alt="Prev"/>
        <button
          className="Back-Button"
          type="button"
          onClick={() => history.goBack(-1)}
        >
          Back
        </button>
      </div>
      <h1 className="CartPage-Title">Cart</h1>
      <div className="CartPage-Content">
        <ul className="CartPage-ContainerCart">
          {cartsList.map(item => (
            <li key={item.id}>
              <ProductCart {...item}/>
            </li>
          ))}
          {/* <ProductCart {...phones[0]}/>
          <ProductCart {...phones[1]}/>
          <ProductCart {...phones[2]}/>
          <ProductCart {...phones[3]}/> */}
        </ul>
        <div className="CartPage-Total">
          <div className="CartPage-CounterPrice">${totalPrice}</div>
          <div className="CartPage-CounterItems">Total for 3 items</div>
          <button className="CartPage-Checkout">Checkout</button>
        </div>
      </div>
    </div>
  )
}
