import React, { useContext } from 'react';
import { ProductsContext } from '../../ProductsProvider';
import { NavLink } from 'react-router-dom';
import { CartProduct } from '../CartProduct';
import './PageContent.scss';

export const PageContent = ({ pro}) => {
  const { favorites, productsList, appliedValue, path } = useContext(ProductsContext);
  const getProductList = () => (
    appliedValue[path]
             ?  productsList
                  .filter(product => favorites.includes(product.id))
                  .filter(product => product.name.toLowerCase().includes(appliedValue[path].toLowerCase()))
             :  productsList.filter(product => favorites.includes(product.id))
  )

  return (
    <div className="PageContent">
      {!appliedValue[path] && (
        <div className="PageContent-Nav">
          <NavLink className="PageContent-Link nav" to="/home">
            <img src="./img/home.svg" alt="Home" className="nav"/>
          </NavLink>
          <img src="./img/next-disabled.svg" alt="Next"/>
          <p className="nav">Favorites</p>
        </div>
      )}
      <div className="PageContent-Main">
        {!appliedValue[path] && (
          <>
            <h1 className="PageContent-Title">Favorites</h1>
            <p className="PageContent-Count">
            {favorites.length} items
            </p>
          </>
        )}
        {appliedValue[path] && (
          <p className="PageContent-Results">
          {getProductList().length} results
          </p>
        )}
        <div className="PageContent-Content">
          { getProductList()
           .map(product => <CartProduct {...product} />)
          }
        </div>

      </div>
    </div>
  )
}
