import React, { useContext } from 'react';
import './CartProduct.scss';
import { NavLink } from 'react-router-dom';
import { ProductsContext } from '../../ProductsProvider';
import { PropTypes } from 'prop-types';

export const CartProduct = ({
  images,
  name,
  price,
  capacity,
  id,
}) => {

  const { favorites, setFavorites, carts, setCarts, setProductId } = useContext(ProductsContext);

  return (
    <div className="CartProduct">
      <NavLink
        className="CartProduct-ImageContainer"
        to={`/product/${id}`}
        onClick={() => {history.push(`/product/${id}`);
        setProductId
      }}
      >
        <img
          className="CartProduct-Image"
          src={images[0]}
          alt="Iphone"
          width="190px"
        />
      </NavLink>
      <div className="CartProduct-Info">
        <h3 className="CartProduct-Name">
          {name}
        </h3>
        <div className="CartProduct-Price">
          <div className="price">{`${price[0]}$`}</div>
          <div className="sale">
            {price[1] ? `${price[1]}$` : ''}
          </div>
        </div>
        <div className="line">{}</div>
        <div className="CartProduct-Description">
          <div className="CartProduct-Item">
            <p className="CartProduct-Screen name">Screen</p>
            <p className="CartProduct-ScreenIndex index">6.5‚Äù OLED</p>
          </div>
          <div className="CartProduct-Item">
            <p className="CartProduct-Capacity name">Capacity</p>
            <p className="CartProduct-CapacityRam index">{capacity}</p>
          </div>
          <div className="CartProduct-Item">
            <p className="CartProduct-Ram name">RAM</p>
            <p className="CartProduct-RamRam index">4 GB</p>
          </div>
        </div>
      </div>
      <div className="CartProduct-Buttons">
        {!carts.includes(id) ? (
          <button
            type="button"
            className="CartProduct-ButtonAdd"
            onClick={() => {setCarts([ ...carts, id ])}}
          >
            Add to cart
          </button>
        ) : (
          <button
            type="button"
            className="CartProduct-ButtonAdded"
          >
            Added to cart
          </button>
        )}
        {!favorites.includes(id) ? (
          <button
          type="button"
          className="CartProduct-ButtonFavorites"
          onClick={() => setFavorites([ ...favorites, id ])}
        >
          <img src="./img/favorites.svg" alt="Favorites"/>
        </button>
        ) : (
          <button
            type="button"
            className="CartProduct-ButtonFavorites CartProduct-ButtonFavorites_selected"
            onClick={() => setFavorites(favorites.filter(item => item !== id))}
          >
            <img src="./img/favorites-selected.svg" alt="Favorites"/>
          </button>
        )}
      </div>
    </div>
  );
}

CartProduct.propTypes = {
  images: PropTypes.arrayOf(PropTypes.string.isRequired,
  ).isRequired,
  name: PropTypes.string.isRequired,
  price: PropTypes.arrayOf(PropTypes.number.isRequired).isRequired,
  capacity: PropTypes.string.isRequired,
  id: PropTypes.number.isRequired,
};
