import React, {useContext} from 'react';
import './CartProduct.scss';
import { useHistory, useRouteMatch } from 'react-router';
import { NavLink } from 'react-router-dom';
import { PropTypes } from 'prop-types';
import { Buttons } from '../Buttons';
//import { ProductsContext } from '../../ProductsProvider';

export const CartProduct = ({
  images,
  name,
  price,
  tech,
  id,
  type
}) => {
  // const { path } = useContext(ProductsContext);

  const history = useHistory();
  const match = useRouteMatch();
  const product = match.params
  console.log(match)

  return (
    <div className="CartProduct">
      <NavLink
        className="CartProduct-ImageContainer"
        to={`${type}/product/${id}`}
        exact
      >
        <img
          className="CartProduct-Image"
          src={images[0]}
          alt="Iphone"
          width="190px"
        />
      </NavLink>
      <div className="CartProduct-Info">
        <h3 className="CartProduct-Name">
          {name}
        </h3>
        <div className="CartProduct-PriceBlock PriceBlock">
          <div className="PriceBlock-Price">{`${price[0]}$`}</div>
          <div className="PriceBlock-Sale">
            {price[1] ? `${price[1]}$` : ''}
          </div>
        </div>
        <div className="line">{}</div>
        <ul className="CartProduct-Details Details">
          {tech.map(([name, value]) => (
            <li className="Details-Item" key={value}>
              <p className="Details-Name">{name}</p>
              <p className="Details-Value">{value}</p>
            </li>
          ))}
        </ul>
      </div>
      <Buttons id={id}/>
    </div>
  );
}

CartProduct.propTypes = {
  images: PropTypes.arrayOf(PropTypes.string.isRequired,
  ).isRequired,
  name: PropTypes.string.isRequired,
  price: PropTypes.arrayOf(PropTypes.number.isRequired).isRequired,
  id: PropTypes.number.isRequired,
  type: PropTypes.string.isRequired,
  tech: PropTypes.arrayOf(PropTypes.array.isRequired,
    ).isRequired,
};
