import React, { useContext } from 'react';
import './Header.scss';
import { Nav } from '../Nav';
import { Favorites } from '../Favorites';
import { Cart } from '../Cart';
import { Search } from '../Search';
import { ProductsContext } from '../../ProductsProvider';

export const Header = () => {
  const { carts, favorites } = useContext(ProductsContext);

  return (
    <div className="Header">
      <div className="Header-SectionLeft">
        <a className="Header-Logo Logo" href="/">
          <img
            className="Logo-Image"
            src="./img/logo.svg"
            alt="Logo"
          />
        </a>
        <Nav />
      </div>
      <div className="Header-SectionRight">
        <Search />
        <ul className="Header-NavListRight NavListRight">
          {['Favorites', 'Cart'].map(item => (
            <li className="NavListRight-Item">
               <NavLink
                  className="NavListRight-Nav"
                  to="/favorites"
                >
                  <div className="NavListRight-NavContent">
                    <img
                      className="NavListRight-Ima"
                      src="./img/favorites.svg"
                      alt="Favorites"
                    />
                    {favorites.length !== 0 && (
                      <span className="SectionRight-Count">{favorites.length}</span>
                    )}
                  </div>
                </NavLink>
            </li>
          ))}
        </ul>
        <Favorites />
        <Cart />
      </div>
    </div>
  )
}
