import React, { useContext, useMemo } from 'react';
import { ProductsContext } from '../../ProductsProvider';
import { useHistory, useLocation } from 'react-router';
import PropTypes from 'prop-types';
import { NavLink } from 'react-router-dom';
import { CartProduct } from '../CartProduct';
import { SelectSort } from '../SelectSort';
import { SelectItems } from '../SelectItems';
import './PageContent.scss';
import { Pagination } from '../Pagination';

export const PageContent = ({ products, title }) => {
  const {
    appliedValue,
    path,
    sortCarts,
    pageItems,
    page,
    setPageItems
  } = useContext(ProductsContext);
  const { search } = useLocation();
  const history = useHistory();
  const searchParams = new URLSearchParams(search);
  const [width, setWidth] = useState(0);
    // if (window.innerWidth <= 767 && ['phones', 'tablets', 'watches'].includes(path)) {
    //   searchParams.delete('perPage');
    //   searchParams.delete('page');
    // }
  // let width = element.getClientRects();
  useMemo(() => window.addEventListener('resize', (e) => {
    console.log(typeof e.target.innerWidth);
    if (e.target.innerWidth <= 767 && ['phones', 'tablets', 'watches'].includes(path)) {
      setWidth()
      setPageItems({...pageItems, [path]: "All items"});

      // searchParams.delete('perPage');
      // searchParams.delete('page');
  }}), [path, window.innerWidth]);

  // console.log(width);
  useMemo(() => {
    if (sortCarts[path]) {
      searchParams.set('sort',
        sortCarts[path] === 'Alphabetically'
        ? 'name': sortCarts[path] === 'Newest'
        ? 'age' : 'price');
    }

    if (!['home', 'favorites', 'cart'].includes(path)) {
      if (pageItems[path] !== 'All items') {
        searchParams.set('perPage', pageItems[path]);
        searchParams.set('page', page[path]);
      } else {
        searchParams.delete('perPage');
        searchParams.delete('page');
      }
    }

    if (!['home', 'cart'].includes(path)) {
      if (appliedValue[path]) {
        searchParams.set('query', appliedValue[path]);
        searchParams.delete('sort');
        searchParams.delete('perPage');
        searchParams.delete('page');
      } else {
        searchParams.delete('query');
      }
    }
    history.push(`${path}?${searchParams.toString()}`);

  },[sortCarts[path], pageItems[path], page[path], appliedValue[path], path]);

  let forPage = ((page[path] - 1) * pageItems[path]);
  let toPage = pageItems[path] * page[path];

  const sortProducts = useMemo(() => {
    switch(sortCarts[path]) {
      case 'Newest':
        return [...products].sort((a, b) => (
          b.model.localeCompare(a.model)));
      case 'Alphabetically':
        return [...products].sort((a, b) => (
          a.name.localeCompare(b.name)));
      case 'Cheapest':
        return [...products].sort((a, b) => (
          a.price[0] - b.price[0]));
      default:
        return products;
    }
  }, [sortCarts[path], products]);

  const visibleProducts = useMemo(() => {
    if ( appliedValue[path]) {
      return products.filter(product => product.name.toLowerCase()
      .includes(appliedValue[path].toLowerCase()))
    }

    if (!appliedValue[path]) {
      if (pageItems[path] !== "All items") {
        return sortProducts.slice(forPage, toPage);
      }
      return sortProducts;
    }
  }, [appliedValue[path],
     sortProducts,
     products,
     pageItems[path],
     forPage,
     toPage]);

  return (
    <div className="PageContent">
      {!appliedValue[path] && (
        <div className="PageContent-Breadcrumbs Breadcrumbs">
          <NavLink className="Breadcrumbs-Item" to="/home">
            <img src="./img/home.svg" alt="Home"/>
          </NavLink>
          <img
            src="./img/next-disabled.svg"
            alt="Next"
            className="Breadcrumbs-Item"
          />
          <p className="Breadcrumbs-Item">
            {path[0].toUpperCase() + path.slice(1)}
          </p>
        </div>
      )}
      <div className="PageContent-Main">
        {!appliedValue[path] && (
          <>
            <h1 className="PageContent-Title">{title}</h1>
            <p className="PageContent-Count">
            {visibleProducts.length} items
            </p>
          </>
        )}
        {appliedValue[path] && (
          <p className="PageContent-Results">
          {visibleProducts.length} results
          </p>
        )}
        {(path !== 'favorites' && !appliedValue[path]) && (
          <div className="PageContent-Selects">
            <div className="PageContent-SelectsContainer">
              <p className="PageContent-SelectName">Sort by</p>
              <div className="PageContent-SelectSort">
                <SelectSort list={['Alphabetically', 'Newest', 'Cheapest']}/>
              </div>
            </div>
            <div className="PageContent-SelectsContainer PageContent-SelectsContainer_hidden">
              <p className="PageContent-SelectName">Items on page</p>
              <div className="PageContent-SelectPageItems">
                <SelectItems list={['All items', '4', '8' , '16']}/>
              </div>
            </div>
          </div>
        )}
        <div className="PageContent-Content">
          { visibleProducts.map(product => <CartProduct {...product} key={product.id}/>)}
        </div>
      </div>
      {path !== 'favorites' && pageItems[path] !== "All items" && (
      <Pagination total={sortProducts.length}/>)}
    </div>
  )
}

PageContent.propTypes = {
  products: PropTypes.array.isRequired,
  title: PropTypes.string.isRequired,
}
