import React, {useEffect, useState, useMemo, useCallback } from 'react';
import './ProductDetailsPage.scss';
import { useHistory, useParams } from 'react-router';
import { NavLink } from 'react-router-dom';
import phones from '../../data/phones.json';
import details from '../../data/details.json';
import classNames from 'classnames';
import { Buttons } from '../Buttons';
//import { useContext } from 'react';
//import { ProductsContext } from '../../ProductsProvider';

export const ProductDetailsPage = () => {
  //const { path } = useContext(ProductsContext);
  const history = useHistory();
  const { productId, typeProduct } = useParams() || '0';
  const [product, setProduct] = useState(phones.find(item => item.id === +productId));
  console.log(typeProduct);
  const [image, setImage] = useState([]);
  const [imageHover, setImageHover] = useState('');
  const [colorList, setColorList] = useState([]);
  const [optionList, setOptionList] = useState([]);
  const { name , images, model, color, id , type, option, price, tech } = product;

  useEffect(() => {
    setProduct(phones.find(item => item.id === +productId));
  }, [productId]);

  useMemo(() => {
    setImage(images[0]);
    if (product) {
      setColorList(details.find(item => item.models === model).colors);
      setOptionList(details.find(item => item.models === model).options);
    }
}, [product]);

  const findProductColor = useCallback((colorItem) => {
    return phones.find(item => item.color === colorItem && item.type === typeProduct && item.option === option && item.model === model).id;
  }, []);

  const findProductOption = useCallback((optionItem) => {
    return phones.find(item => item.color === color && item.type === typeProduct && item.option === optionItem && item.model === model).id;
  }, []);

  const description = useMemo(() => (
    [...details].find(item => item.models === model && item.type === type).about
  ), [product]);

  console.log(description);

  return (
    <>
    {productId && (
    <div className="ProductDetailsPage">
      <div className="ProductDetailsPage-Breadcrumbs Breadcrumbs">
        <div className="Breadcrumbs-List">
          <NavLink className="Breadcrumbs-Item" to="/home">
            <img src="./img/home.svg" alt="Home"/>
          </NavLink>
          <img src="./img/next-disabled.svg" alt="Next" className="Breadcrumbs-Item"/>
          <NavLink className="Breadcrumbs-Item text" to={`/${type}`}>
            {type[0].toUpperCase() + type.slice(1)}
          </NavLink>
          <img src="./img/next-disabled.svg" alt="Next" className="Breadcrumbs-Item"/>
          <p className="Breadcrumbs-Item text">{name}</p>
        </div>
        <div className="Breadcrumbs-Back">
          <img src="./img/prev.svg" alt="Prev"/>
          <button
            className="Breadcrumbs-Button"
            type="button"
            onClick={() => history.goBack(-1)}
          >
            Back
          </button>
        </div>
      </div>
      <h1 className="ProductDetailsPage-Title">{name}</h1>
      <div className="ProductDetailsPage-ContainerTop">
        <div className="ProductDetailsPage-ImagesBlock ImagesBlock">
          <ul className="ImagesBlock-List">
            {images.map(item => (
              <li
                key={item}
                className={classNames('ImagesBlock-Item', {
                  item_active: item === image,
                  item_size: model === "11 Pro"
                })}
                onClick={() => setImage(item)}
                onMouseEnter={() => setImageHover(item)}
                onMouseLeave={() => setImageHover('')}
              >
                <img
                  src={item}
                  alt="Product"
                  width="64px"
                  height="64px"
                  className="itemImage"
                />
              </li>
            ))}
          </ul>
          <img
            src={imageHover ? imageHover : image}
            alt="Product"
            width="200px"
            height="200px"
            className="ImagesBlock-Image"
          />
        </div>
        <div className="ProductDetailsPage-OptionsBlock">
          <div className="ProductDetailsPage-Colors Colors">
            <h3 className="Colors-Title">Available colors</h3>
            <ul className="Colors-List">
              {colorList.map(([item, hex ]) => (
                <li
                  key={item}
                  className={classNames('Colors-Item', {
                    color_active: item === color,
                  })}
                  onClick={() => history.push(
                    `/${type}/product/${findProductColor(item)}`
                  )}
                >
                  <div
                    className="color"
                    style={{
                      background: `${hex}`,
                      border: `${1}px solid ${hex}`
                    }}
                  >{}</div>
                </li>
              ))}
            </ul>
          </div>
          <div className="ProductDetailsPage-Options Options">
            <h3 className="Options-Title">
              Select {`${typeProduct !== 'watches' ? 'capacity' : 'size'}`}
            </h3>
            <ul className="Options-List">
              {optionList.map(item => (
                <li
                  key={item}
                  className={classNames('Options-Item', {
                    option_active: item === option,
                  })}
                  onClick={() => history.push(
                    `/${type}/product/${findProductOption(item)}`
                  )}
                >
                  {item}
                </li>
              ))}
            </ul>
          </div>
          <div className="ProductDetailsPage-PriceBlock PriceBlock">
            <div className="PriceBlock-Price PriceBlock-Price_big-size">
              {`${price[0]}$`}
            </div>
            <div className="PriceBlock-Sale">
              {price[1] ? `${price[1]}$` : ''}
            </div>
          </div>
          <div className="ProductDetailsPage-Buttons ProductDetailsPage-Buttons_width"><Buttons id={id}/></div>
          <ul className="ProductDetailsPage-Details Details">
            {tech.map(([name, value]) => (
              <li className="Details-Item" key={value}>
                <p className="Details-Name">{name}</p>
                <p className="Details-Value">{value}</p>
              </li>
            ))}
          </ul>
        </div>
        <div className="ProductDetailsPage-ID">ID: {id}</div>
      </div>
      <div className="ProductDetailsPage-ContainerBottom">
        <div className="ProductDetailsPage-About About">
          <h2 className="About-ContentTitle">About</h2>
          <ul className="About-ContentList">
            {description.map(([title, text]) => (
               <li className="About-ContentItem" key={title}>
                 <h3 className="About-Title">{title}</h3>
                 <p className="About-Text"></p>
               </li>
            ))}

          </ul>

        </div>
      </div>
    </div>
  )}
  </>
  )
}
