import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import classNames from 'classnames';
import './Search.scss';

export const Search = () => {
  const { pathname } = useLocation();
  console.log(pathname)
  const [value, setValue] = useState({
    phones: '',
    tablets: '',
    accessories: '',
    favorites: '',
  });

  console.log(value);

  const setValueInput = () => {
    switch(pathname) {
      case '/phones':
        return value.phones;
      case '/tablets':
        return value.tablets;
      case '/accessories':
        return value.accessories;
      case '/favorites':
        return value.favorites;
      default:
        break;
    }
  }

  return (
    <div className={classNames('Search', {
      search_hidden: ''

    })}>
      <input
        className="Search-Input"
        type="text"
        name={pathname.slice(1)}
        placeholder={`Search in ${pathname.slice(1)}...`}
        value={setValueInput()}
        onChange={e => setValue({
          ...value,
          [pathname.slice(1)]: e.target.value,
        })}
      />
      {value[pathname.slice(1)] ? (
        <button
        className="Search-ButtonClear button-search"
        type="button"
        onClick={() => setValue({...value, [pathname.slice(1)]: ''})}
        tabIndex
      >
        <img
          src="./img/clear.svg"
          alt="Clear"
        />
      </button>
      ) : (
      <button
        className="Search-ButtonSearch button-search"
        type="button"
        tabindex="0"
        onClick={() => {console.log('hello')}}
        tabindex="0"
      >
        <img
          src="./img/search.svg"
          alt="Search"
        />
      </button>
      )}
    </div>
  )
}
