export const getSliderWidth = () => (
  window.innerWidth > 655 ? Math.ceil((window.innerWidth * 72.2/100)+ 96) : Math.ceil(window.innerWidth - 48)
)

export const getCartLength = () => {
  let cartWidth = 272;
  let width = getSliderWidth() ;
  return (width / cartWidth);
}

export const getMargin = () => {
  let cartWidth = 272;
  let width = getSliderWidth();
  let cartLength = getCartLength();
  return Math(width - (cartWidth * cartLength)) / (cartLength - 1)
}

export const getMarginLeft = () => {
  let cartWidth = 272;
  let width = getSliderWidth();
  return width < 655 ? width - cartWidth : 0;
}
