import React, { useState } from 'react';
import './HomePage.scss';

const initialImages = [
  './img/slider/1.jpg',
  './img/slider/2.jpg',
  // './img/slider/3.jpg',
  // './img/slider/4.jpg',
  // './img/slider/5.jpg',
  // './img/slider/6.jpg',
];


export const HomePage = () => {
  const [images, setImages] = useState(initialImages);
  const [ imageWidth, setImageWidth] = useState(document.documentElement.clientWidth * 72.2 / 100)
  const [ currentPosition, setCurrentPosition] =

  console.log(imageWidth);
  handleClickNext = () => {
    const {
      images,
      itemWidth,
      step,
      frameSize,
      infinite,
      currentPosition,
    } = this.state;
    const maxPosition = (images.length - frameSize) * itemWidth;
    let position = currentPosition - itemWidth * step;

    if (-position > maxPosition && !infinite) {
      position = -maxPosition;
    }

    if (infinite) {
      images.push(...images.splice(0, step));
    }

    this.setState({ currentPosition: position });
  };

  return (
    <div className="HomePage">
      <div className="ProductsSlider">
        <button className="ProductsSlider-Button">
          <img src="./img/prev.svg" alt="Prev"/>
        </button>
        <ul className="ProductsSlider-List">
          {images.map(image => (
            <li className="ProductsSlider-Item" key={image}>
              <img
                src={image}
                alt={image}
                className="image"
              />
            </li>
          ))}
        </ul>
        <button className="ProductsSlider-Button">
          <img src="./img/next.svg" alt="Next"/>
        </button>
      </div>

    </div>

  )
}
