import React, { useState, useEffect, useContext } from 'react';
import PropTypes from 'prop-types';
import './ProductCart.scss';
import phones from '../../data/phones.json';
import { ProductsContext } from '../../ProductsProvider';

export const ProductCart = ({ name, price, image, id, count, quantity, setQuantity }) => {
  //const [quantity, setQuantity] = useState(1);
  // const [quantity, setQuantity] = useState(
  //   JSON.parse(localStorage.getItem('quantity')) || {[id]: 1}
  const { setCarts, carts } = useContext(ProductsContext);
  // );
  // useEffect(() => setQuantity({...quantity, [id]: 1}), []);

  return (
    <div className="ProductCart">
      <div className="ProductCart-ContainerLeft">
        <button
          className="ProductCart-Close"
          onClick={() => setCarts(carts.filter(item => item.id !== id))}
        >
          {}
        </button>
        <img
          className="ProductCart-Image"
          src={image}
          width="66px"
          height="66px"
          alt="Phone"
        />
        <p className="ProductCart-Name">{name}</p>
      </div>
      <div className="ProductCart-ContainerRight">
        <div className="ProductCart-ContainerQuantity">
          <button className="ProductCart-Remove buttonProduct" disabled={true}>{}</button>
          <div className="ProductCart-Quantity">{}</div>
          <button
            className="ProductCart-Add buttonProduct"
            onClick={() => {setCarts([...carts.map(item => item.id === id)])}}
          >{}</button>
        </div>
        <div className="ProductCart-Price">${price}</div>
      </div>
    </div>
  )
}

ProductCart.propTypes = {
  image: PropTypes.string.isRequired,
  name: PropTypes.string.isRequired,
  price: PropTypes.number.isRequired,
  id: PropTypes.number.isRequired,
  quantity: PropTypes.object.isRequired,
  setQuantity: PropTypes.func.isRequired,
  count: PropTypes.number.isRequired,

  // type: PropTypes.string.isRequired,
  // tech: PropTypes.arrayOf(PropTypes.array.isRequired,
  //   ).isRequired,
};
