import React, { useContext } from 'react';
import classNames from 'classnames';
import PropTypes from 'prop-types';
import './SelectSort.scss';
import { ProductsContext } from '../../ProductsProvider';

export const SelectSort = ({ list }) => {
  const {
    path,
    toggleSort,
    setToggleSort,
    sortCarts,
    setSortCarts
  } = useContext(ProductsContext);

  return (
    <div className="Select">
      <button
        className={classNames('Select-Button', {
          image_rotate: toggleSort[path],
        })}
        type="button"
        onClick={() => setToggleSort({...toggleSort, [path]: true})}
      >
        <p className="Select-Text">{sortCarts[path]}</p>
        <img src="./img/down.svg" alt="Down" className="button-image"/>
      </button>
      {toggleSort[path] && (
        <ul className={classNames('Select-List', {
          visible_list: !toggleSort[path],
        })}
        >
          {list.map(item => (
            <li
              className={classNames('Select-Item', {
                selected: item === sortCarts[path],
              })}
              key={item}
              onClick={() => setSortCarts({...sortCarts, [path]: item})}
            >
              {item}
            </li>
          ))}
        </ul>
      )}



